pragma solidity >=0.8.0 <0.9.0;

import "./core/Strategy.sol";
import "./exchanges/BeethovenxExchange.sol";
import "./connectors/beethovenx/interfaces/IVault.sol";
import "./connectors/beethovenx/interfaces/IAsset.sol";
import "./connectors/beethovenx/BeethovenxMasterChef.sol";

contract StrategyBeethovenxDeiUsdc is Strategy, BeethovenExchange {

    IERC20 public usdcToken;
    IERC20 public bptDeiUsdcToken;
    IERC20 public beetsToken;
    IERC20 public deusToken;
    IERC20 public wFtmToken;

    uint256 public usdcTokenDenominator;
    uint256 public bptDeiUsdcTokenDenominator;
    uint256 public beetsTokenDenominator;
    uint256 public deusTokenDenominator;
    uint256 public wFtmTokenDenominator;

    IVault public beethovenxVault;
    BeethovenxMasterChef public beethovenxMasterChef;

    bytes32 public poolIdDeiUsdc;
    bytes32 public poolIdBeetsWFtm;
    bytes32 public poolIdDeusWFtm;
    bytes32 public poolIdWFtmUsdc;

    uint256 public pidDeiUsdc;


    // --- events

    event StrategyBalancerUpdatedTokens(
        address usdcToken,
        address bptDeiUsdcToken,
        address beetsToken,
        address deusToken,
        address wFtmToken,
        uint256 usdcTokenDenominator,
        uint256 bptDeiUsdcTokenDenominator,
        uint256 beetsTokenDenominator,
        uint256 deusTokenDenominator,
        uint256 wFtmTokenDenominator
    );

    event StrategyBalancerUpdatedParams(
        address beethovenxVault,
        address beethovenxMasterChef,
        bytes32 poolIdDeiUsdc,
        bytes32 poolIdBeetsWFtm,
        bytes32 poolIdDeusWFtm,
        bytes32 poolIdWFtmUsdc,
        uint256 pidDeiUsdc
    );


    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() initializer {}

    function initialize() initializer public {
        __Strategy_init();
    }


    // --- Setters

    function setTokens(
        address _usdcToken,
        address _bptDeiUsdcToken,
        address _beetsToken,
        address _deusToken,
        address _wFtmToken
    ) external onlyAdmin {

        require(_usdcToken != address(0), "Zero address not allowed");
        require(_bptDeiUsdcToken != address(0), "Zero address not allowed");
        require(_beetsToken != address(0), "Zero address not allowed");
        require(_deusToken != address(0), "Zero address not allowed");
        require(_wFtmToken != address(0), "Zero address not allowed");

        usdcToken = IERC20(_usdcToken);
        bptDeiUsdcToken = IERC20(_bptDeiUsdcToken);
        beetsToken = IERC20(_beetsToken);
        deusToken = IERC20(_deusToken);
        wFtmToken = IERC20(_wFtmToken);

        usdcTokenDenominator = 10 ** IERC20Metadata(_usdcToken).decimals();
        bptDeiUsdcTokenDenominator = 10 ** IERC20Metadata(_bptDeiUsdcToken).decimals();
        beetsTokenDenominator = 10 ** IERC20Metadata(_beetsToken).decimals();
        deusTokenDenominator = 10 ** IERC20Metadata(_deusToken).decimals();
        wFtmTokenDenominator = 10 ** IERC20Metadata(_wFtmToken).decimals();

        emit StrategyBalancerUpdatedTokens(
            _usdcToken,
            _bptDeiUsdcToken,
            _beetsToken,
            _deusToken,
            _wFtmToken,
            usdcTokenDenominator,
            bptDeiUsdcTokenDenominator,
            beetsTokenDenominator,
            deusTokenDenominator,
            wFtmTokenDenominator
        );
    }

    function setParams(
        address _beethovenxVault,
        address _beethovenxMasterChef,
        bytes32 _poolIdDeiUsdc,
        bytes32 _poolIdBeetsWFtm,
        bytes32 _poolIdDeusWFtm,
        bytes32 _poolIdWFtmUsdc,
        uint256 _pidDeiUsdc
    ) external onlyAdmin {

        require(_beethovenxVault != address(0), "Zero address not allowed");
        require(_beethovenxMasterChef != address(0), "Zero address not allowed");
        require(_poolIdDeiUsdc != "", "Empty pool id not allowed");
        require(_poolIdBeetsWFtm != "", "Empty pool id not allowed");
        require(_poolIdDeusWFtm != "", "Empty pool id not allowed");
        require(_poolIdWFtmUsdc != "", "Empty pool id not allowed");

        setBeethovenxVault(_beethovenxVault);

        beethovenxVault = IVault(_beethovenxVault);
        beethovenxMasterChef = BeethovenxMasterChef(_beethovenxMasterChef);
        poolIdDeiUsdc = _poolIdDeiUsdc;
        poolIdBeetsWFtm = _poolIdBeetsWFtm;
        poolIdDeusWFtm = _poolIdDeusWFtm;
        poolIdWFtmUsdc = _poolIdWFtmUsdc;
        pidDeiUsdc = _pidDeiUsdc;

        emit StrategyBalancerUpdatedParams(
            _beethovenxVault,
            _beethovenxMasterChef,
            _poolIdDeiUsdc,
            _poolIdBeetsWFtm,
            _poolIdDeusWFtm,
            _poolIdWFtmUsdc,
            _pidDeiUsdc
        );
    }


    // --- logic

    function _stake(
        address _asset,
        uint256 _amount
    ) internal override {

        require(_asset == address(usdcToken), "Some token not compatible");

        usdcToken.approve(address(beethovenxVault), _amount);

        (IERC20[] memory tokens, uint256[] memory balances, uint256 lastChangeBlock) = beethovenxVault.getPoolTokens(poolIdDeiUsdc);

        IAsset[] memory assets = new IAsset[](2);
        uint256[] memory maxAmountsIn = new uint256[](2);
        uint256[] memory amountsIn = new uint256[](2);
        for (uint256 i; i < tokens.length; i++) {
            assets[i] = IAsset(address(tokens[i]));
            if (tokens[i] == usdcToken) {
                maxAmountsIn[i] = _amount;
                amountsIn[i] = _amount;
            } else {
                maxAmountsIn[i] = 0;
                amountsIn[i] = 0;
            }
        }

        uint256 joinKind = 1;
        uint256 minimumBPT = 0;
        bytes memory userData = abi.encode(joinKind, amountsIn, minimumBPT);

        IVault.JoinPoolRequest memory request = IVault.JoinPoolRequest(assets, maxAmountsIn, userData, false);

        beethovenxVault.joinPool(poolIdDeiUsdc, address(this), address(this), request);

        uint256 amountBptDeiUsdc = bptDeiUsdcToken.balanceOf(address(this));

        bptDeiUsdcToken.approve(address(beethovenxMasterChef), amountBptDeiUsdc);

        beethovenxMasterChef.deposit(pidDeiUsdc, amountBptDeiUsdc, address(this));
    }

    function _unstake(
        address _asset,
        uint256 _amount,
        address _beneficiary
    ) internal override returns (uint256) {

        require(_asset == address(usdcToken), "Some token not compatible");

        // 18 = 18 + 6 - 6
        uint256 amountBptDeiUsdc = bptDeiUsdcTokenDenominator * _amount / _getBptDeiUsdcSellPrice(bptDeiUsdcTokenDenominator);

        beethovenxMasterChef.withdrawAndHarvest(pidDeiUsdc, amountBptDeiUsdc, address(this));

        amountBptDeiUsdc = bptDeiUsdcToken.balanceOf(address(this));

        (IERC20[] memory tokens, uint256[] memory balances, uint256 lastChangeBlock) = beethovenxVault.getPoolTokens(poolIdDeiUsdc);

        IAsset[] memory assets = new IAsset[](2);
        uint256[] memory minAmountsOut = new uint256[](2);
        uint256 exitTokenIndex;
        for (uint256 i; i < tokens.length; i++) {
            assets[i] = IAsset(address(tokens[i]));
            if (tokens[i] == usdcToken) {
                //TODO: Balancer. FIX if big slippage
                minAmountsOut[i] = _amount * 99 / 100;
                exitTokenIndex = i;
            } else {
                minAmountsOut[i] = 0;
            }
        }

        uint256 exitKind = 0;
        bytes memory userData = abi.encode(exitKind, amountBptDeiUsdc, exitTokenIndex);

        IVault.ExitPoolRequest memory request = IVault.ExitPoolRequest(assets, minAmountsOut, userData, false);

        beethovenxVault.exitPool(poolIdDeiUsdc, address(this), payable(address(this)), request);

        return usdcToken.balanceOf(address(this));
    }

    function _unstakeFull(
        address _asset,
        address _beneficiary
    ) internal override returns (uint256) {

        require(_asset == address(usdcToken), "Some token not compatible");

        (uint256 amountBptDeiUsdc, uint256 rewardDebt) = beethovenxMasterChef.userInfo(pidDeiUsdc, address(this));

        beethovenxMasterChef.withdrawAndHarvest(pidDeiUsdc, amountBptDeiUsdc, address(this));

        uint256 amountUsdc = _getBptDeiUsdcSellPrice(amountBptDeiUsdc);

        (IERC20[] memory tokens, uint256[] memory balances, uint256 lastChangeBlock) = beethovenxVault.getPoolTokens(poolIdDeiUsdc);

        IAsset[] memory assets = new IAsset[](2);
        uint256[] memory minAmountsOut = new uint256[](2);
        uint256 exitTokenIndex = 0;
        for (uint256 i; i < tokens.length; i++) {
            assets[i] = IAsset(address(tokens[i]));
            if (tokens[i] == usdcToken) {
                //TODO: Balancer. FIX if big slippage
                minAmountsOut[i] = amountUsdc * 99 / 100;
                exitTokenIndex = i;
            } else {
                minAmountsOut[i] = 0;
            }
        }

        uint256 exitKind = 0;
        bytes memory userData = abi.encode(exitKind, amountBptDeiUsdc, exitTokenIndex);

        IVault.ExitPoolRequest memory request = IVault.ExitPoolRequest(assets, minAmountsOut, userData, false);

        beethovenxVault.exitPool(poolIdDeiUsdc, address(this), payable(address(this)), request);

        return usdcToken.balanceOf(address(this));
    }

    function netAssetValue() external override view returns (uint256) {
        (uint256 amount, uint256 rewardDebt) = beethovenxMasterChef.userInfo(pidDeiUsdc, address(this));
        if (amount == 0) {
            return 0;
        }
        return _getBptDeiUsdcBuyPrice(amount);
    }

    function liquidationValue() external override view returns (uint256) {
        (uint256 amount, uint256 rewardDebt) = beethovenxMasterChef.userInfo(pidDeiUsdc, address(this));
        if (amount == 0) {
            return 0;
        }
        return _getBptDeiUsdcSellPrice(amount);
    }

    function _getBptDeiUsdcBuyPrice(uint256 balanceBptDeiUsdc) internal view returns (uint256) {
        uint256 totalSupply = bptDeiUsdcToken.totalSupply();

        uint256 totalBalanceUsdc;
        (IERC20[] memory tokens, uint256[] memory balances, uint256 lastChangeBlock) = beethovenxVault.getPoolTokens(poolIdDeiUsdc);
        for (uint256 i; i < tokens.length; i++) {
            uint256 tokenBalance = balances[i] * balanceBptDeiUsdc / totalSupply;
            if (tokens[i] != usdcToken) {
                totalBalanceUsdc += onSwap(poolIdDeiUsdc, IVault.SwapKind.GIVEN_OUT, usdcToken, tokens[i], tokenBalance);
            } else {
                totalBalanceUsdc += tokenBalance;
            }
        }

        return totalBalanceUsdc;
    }

    function _getBptDeiUsdcSellPrice(uint256 balanceBptDeiUsdc) internal view returns (uint256) {
        uint256 totalSupply = bptDeiUsdcToken.totalSupply();

        uint256 totalBalanceUsdc;
        (IERC20[] memory tokens, uint256[] memory balances, uint256 lastChangeBlock) = beethovenxVault.getPoolTokens(poolIdDeiUsdc);
        for (uint256 i; i < tokens.length; i++) {
            uint256 tokenBalance = balances[i] * balanceBptDeiUsdc / totalSupply;
            if (tokens[i] != usdcToken) {
                totalBalanceUsdc += onSwap(poolIdDeiUsdc, IVault.SwapKind.GIVEN_IN, tokens[i], usdcToken, tokenBalance);
            } else {
                totalBalanceUsdc += tokenBalance;
            }
        }

        return totalBalanceUsdc;
    }

    function _claimRewards(address _to) internal override returns (uint256) {

        beethovenxMasterChef.harvest(pidDeiUsdc, address(this));

        uint256 totalUsdc;

        uint256 beetsBalance = beetsToken.balanceOf(address(this));
        if (beetsBalance > 0) {
            uint256 beetsUsdc = batchSwap(poolIdBeetsWFtm, poolIdWFtmUsdc, IVault.SwapKind.GIVEN_IN, IAsset(address(beetsToken)),
                IAsset(address(wFtmToken)), IAsset(address(usdcToken)), address(this), payable(address(this)), beetsBalance);
            totalUsdc += beetsUsdc;
        }

        uint256 deusBalance = deusToken.balanceOf(address(this));
        if (deusBalance > 0) {
            uint256 deusUsdc = batchSwap(poolIdDeusWFtm, poolIdWFtmUsdc, IVault.SwapKind.GIVEN_IN, IAsset(address(deusToken)),
                IAsset(address(wFtmToken)), IAsset(address(usdcToken)), address(this), payable(address(this)), deusBalance);
            totalUsdc += deusUsdc;
        }

        usdcToken.transfer(_to, usdcToken.balanceOf(address(this)));

        return totalUsdc;
    }

}","keccak256":"0x93da9e303781add86cb5ae7698fcc52db58e1f928e6f458bff6108b9d976a6c9","license":"MIT"},"contracts/connectors/beethovenx/BeethovenxMasterChef.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity >=0.8.0 <0.9.0;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "./BeethovenxToken.sol";
import "./interfaces/IRewarder.sol";

/*
    This master chef is based on SUSHI's version with some adjustments:
     - Upgrade to pragma 0.8.7
     - therefore remove usage of SafeMath (built in overflow check for solidity > 8)
     - Merge sushi's master chef V1 & V2 (no usage of dummy pool)
     - remove withdraw function (without harvest) => requires the rewardDebt to be an signed int instead of uint which requires a lot of casting and has no real usecase for us
     - no dev emissions, but treasury emissions instead
     - treasury percentage is subtracted from emissions instead of added on top
     - update of emission rate with upper limit of 6 BEETS/block
     - more require checks in general
*/

// Have fun reading it. Hopefully it's still bug-free
contract BeethovenxMasterChef is Ownable {
    using SafeERC20 for IERC20;
    using EnumerableSet for EnumerableSet.AddressSet;

    // Info of each user.
    struct UserInfo {
        uint256 amount; // How many LP tokens the user has provided.
        uint256 rewardDebt; // Reward debt. See explanation below.
        //
        // We do some fancy math here. Basically, any point in time, the amount of BEETS
        // entitled to a user but is pending to be distributed is:
        //
        //   pending reward = (user.amount * pool.accBeetsPerShare) - user.rewardDebt
        //
        // Whenever a user deposits or withdraws LP tokens to a pool. Here's what happens:
        //   1. The pool's `accBeetsPerShare` (and `lastRewardBlock`) gets updated.
        //   2. User receives the pending reward sent to his/her address.
        //   3. User's `amount` gets updated.
        //   4. User's `rewardDebt` gets updated.
    }
    // Info of each pool.
    struct PoolInfo {
        // we have a fixed number of BEETS tokens released per block, each pool gets his fraction based on the allocPoint
        uint256 allocPoint; // How many allocation points assigned to this pool. the fraction BEETS to distribute per block.
        uint256 lastRewardBlock; // Last block number that BEETS distribution occurs.
        uint256 accBeetsPerShare; // Accumulated BEETS per LP share. this is multiplied by ACC_BEETS_PRECISION for more exact results (rounding errors)
    }
    // The BEETS TOKEN!
    BeethovenxToken public beets;

    // Treasury address.
    address public treasuryAddress;

    // BEETS tokens created per block.
    uint256 public beetsPerBlock;

    uint256 private constant ACC_BEETS_PRECISION = 1e12;

    // distribution percentages: a value of 1000 = 100%
    // 12.8% percentage of pool rewards that goes to the treasury.
    uint256 public constant TREASURY_PERCENTAGE = 128;

    // 87.2% percentage of pool rewards that goes to LP holders.
    uint256 public constant POOL_PERCENTAGE = 872;

    // Info of each pool.
    PoolInfo[] public poolInfo;
    // Info of each user that stakes LP tokens per pool. poolId => address => userInfo
    /// @notice Address of the LP token for each MCV pool.
    IERC20[] public lpTokens;

    EnumerableSet.AddressSet private lpTokenAddresses;

    /// @notice Address of each `IRewarder` contract in MCV.
    IRewarder[] public rewarder;

    mapping(uint256 => mapping(address => UserInfo)) public userInfo; // mapping form poolId => user Address => User Info
    // Total allocation points. Must be the sum of all allocation points in all pools.
    uint256 public totalAllocPoint = 0;
    // The block number when BEETS mining starts.
    uint256 public startBlock;

    event Deposit(
        address indexed user,
        uint256 indexed pid,
        uint256 amount,
        address indexed to
    );
    event Withdraw(
        address indexed user,
        uint256 indexed pid,
        uint256 amount,
        address indexed to
    );
    event EmergencyWithdraw(
        address indexed user,
        uint256 indexed pid,
        uint256 amount,
        address indexed to
    );
    event Harvest(address indexed user, uint256 indexed pid, uint256 amount);
    event LogPoolAddition(
        uint256 indexed pid,
        uint256 allocPoint,
        IERC20 indexed lpToken,
        IRewarder indexed rewarder
    );
    event LogSetPool(
        uint256 indexed pid,
        uint256 allocPoint,
        IRewarder indexed rewarder,
        bool overwrite
    );
    event LogUpdatePool(
        uint256 indexed pid,
        uint256 lastRewardBlock,
        uint256 lpSupply,
        uint256 accBeetsPerShare
    );
    event SetTreasuryAddress(
        address indexed oldAddress,
        address indexed newAddress
    );
    event UpdateEmissionRate(address indexed user, uint256 _beetsPerSec);

    constructor(
        BeethovenxToken _beets,
        address _treasuryAddress,
        uint256 _beetsPerBlock,
        uint256 _startBlock
    ) {
        require(
            _beetsPerBlock <= 6e18,
            "maximum emission rate of 6 beets per block exceeded"
        );
        beets = _beets;
        treasuryAddress = _treasuryAddress;
        beetsPerBlock = _beetsPerBlock;
        startBlock = _startBlock;
    }

    function poolLength() external view returns (uint256) {
        return poolInfo.length;
    }

    // Add a new lp to the pool. Can only be called by the owner.
    function add(
        uint256 _allocPoint,
        IERC20 _lpToken,
        IRewarder _rewarder
    ) public onlyOwner {
        require(
            Address.isContract(address(_lpToken)),
            "add: LP token must be a valid contract"
        );
        require(
            Address.isContract(address(_rewarder)) ||
            address(_rewarder) == address(0),
            "add: rewarder must be contract or zero"
        );
        // we make sure the same LP cannot be added twice which would cause trouble
        require(
            !lpTokenAddresses.contains(address(_lpToken)),
            "add: LP already added"
        );

        // respect startBlock!
        uint256 lastRewardBlock = block.number > startBlock
        ? block.number
        : startBlock;
        totalAllocPoint = totalAllocPoint + _allocPoint;

        // LP tokens, rewarders & pools are always on the same index which translates into the pid
        lpTokens.push(_lpToken);
        lpTokenAddresses.add(address(_lpToken));
        rewarder.push(_rewarder);

        poolInfo.push(
            PoolInfo({
        allocPoint: _allocPoint,
        lastRewardBlock: lastRewardBlock,
        accBeetsPerShare: 0
        })
        );
        emit LogPoolAddition(
            lpTokens.length - 1,
            _allocPoint,
            _lpToken,
            _rewarder
        );
    }

    // Update the given pool's BEETS allocation point. Can only be called by the owner.
    /// @param _pid The index of the pool. See `poolInfo`.
    /// @param _allocPoint New AP of the pool.
    /// @param _rewarder Address of the rewarder delegate.
    /// @param overwrite True if _rewarder should be `set`. Otherwise `_rewarder` is ignored.
    function set(
        uint256 _pid,
        uint256 _allocPoint,
        IRewarder _rewarder,
        bool overwrite
    ) public onlyOwner {
        require(
            Address.isContract(address(_rewarder)) ||
            address(_rewarder) == address(0),
            "set: rewarder must be contract or zero"
        );

        // we re-adjust the total allocation points
        totalAllocPoint =
        totalAllocPoint -
        poolInfo[_pid].allocPoint +
        _allocPoint;

        poolInfo[_pid].allocPoint = _allocPoint;

        if (overwrite) {
            rewarder[_pid] = _rewarder;
        }
        emit LogSetPool(
            _pid,
            _allocPoint,
            overwrite ? _rewarder : rewarder[_pid],
            overwrite
        );
    }

    // View function to see pending BEETS on frontend.
    function pendingBeets(uint256 _pid, address _user)
    external
    view
    returns (uint256 pending)
    {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][_user];
        // how many BEETS per lp token
        uint256 accBeetsPerShare = pool.accBeetsPerShare;
        // total staked lp tokens in this pool
        uint256 lpSupply = lpTokens[_pid].balanceOf(address(this));

        if (block.number > pool.lastRewardBlock && lpSupply != 0) {
            uint256 blocksSinceLastReward = block.number - pool.lastRewardBlock;
            // based on the pool weight (allocation points) we calculate the beets rewarded for this specific pool
            uint256 beetsRewards = (blocksSinceLastReward *
            beetsPerBlock *
            pool.allocPoint) / totalAllocPoint;

            // we take parts of the rewards for treasury, these can be subject to change, so we recalculate it
            // a value of 1000 = 100%
            uint256 beetsRewardsForPool = (beetsRewards * POOL_PERCENTAGE) /
            1000;

            // we calculate the new amount of accumulated beets per LP token
            accBeetsPerShare =
            accBeetsPerShare +
            ((beetsRewardsForPool * ACC_BEETS_PRECISION) / lpSupply);
        }
        // based on the number of LP tokens the user owns, we calculate the pending amount by subtracting the amount
        // which he is not eligible for (joined the pool later) or has already harvested
        pending =
        (user.amount * accBeetsPerShare) /
        ACC_BEETS_PRECISION -
        user.rewardDebt;
    }

    /// @notice Update reward variables for all pools. Be careful of gas spending!
    /// @param pids Pool IDs of all to be updated. Make sure to update all active pools.
    function massUpdatePools(uint256[] calldata pids) external {
        uint256 len = pids.length;
        for (uint256 i = 0; i < len; ++i) {
            updatePool(pids[i]);
        }
    }

    // Update reward variables of the given pool to be up-to-date.
    function updatePool(uint256 _pid) public returns (PoolInfo memory pool) {
        pool = poolInfo[_pid];

        if (block.number > pool.lastRewardBlock) {
            // total lp tokens staked for this pool
            uint256 lpSupply = lpTokens[_pid].balanceOf(address(this));
            if (lpSupply > 0) {
                uint256 blocksSinceLastReward = block.number -
                pool.lastRewardBlock;

                // rewards for this pool based on his allocation points
                uint256 beetsRewards = (blocksSinceLastReward *
                beetsPerBlock *
                pool.allocPoint) / totalAllocPoint;

                uint256 beetsRewardsForPool = (beetsRewards * POOL_PERCENTAGE) /
                1000;

                beets.mint(
                    treasuryAddress,
                    (beetsRewards * TREASURY_PERCENTAGE) / 1000
                );

                beets.mint(address(this), beetsRewardsForPool);

                pool.accBeetsPerShare =
                pool.accBeetsPerShare +
                ((beetsRewardsForPool * ACC_BEETS_PRECISION) / lpSupply);
            }
            pool.lastRewardBlock = block.number;
            poolInfo[_pid] = pool;

            emit LogUpdatePool(
                _pid,
                pool.lastRewardBlock,
                lpSupply,
                pool.accBeetsPerShare
            );
        }
    }

    // Deposit LP tokens to MasterChef for BEETS allocation.
    function deposit(
        uint256 _pid,
        uint256 _amount,
        address _to
    ) public {
        PoolInfo memory pool = updatePool(_pid);
        UserInfo storage user = userInfo[_pid][_to];

        user.amount = user.amount + _amount;
        // since we add more LP tokens, we have to keep track of the rewards he is not eligible for
        // if we would not do that, he would get rewards like he added them since the beginning of this pool
        // note that only the accBeetsPerShare have the precision applied
        user.rewardDebt =
        user.rewardDebt +
        (_amount * pool.accBeetsPerShare) /
        ACC_BEETS_PRECISION;

        IRewarder _rewarder = rewarder[_pid];
        if (address(_rewarder) != address(0)) {
            _rewarder.onBeetsReward(_pid, _to, _to, 0, user.amount);
        }

        lpTokens[_pid].safeTransferFrom(msg.sender, address(this), _amount);

        emit Deposit(msg.sender, _pid, _amount, _to);
    }

    function harvestAll(uint256[] calldata _pids, address _to) external {
        for (uint256 i = 0; i < _pids.length; i++) {
            if (userInfo[_pids[i]][msg.sender].amount > 0) {
                harvest(_pids[i], _to);
            }
        }
    }

    /// @notice Harvest proceeds for transaction sender to `_to`.
    /// @param _pid The index of the pool. See `poolInfo`.
    /// @param _to Receiver of BEETS rewards.
    function harvest(uint256 _pid, address _to) public {
        PoolInfo memory pool = updatePool(_pid);
        UserInfo storage user = userInfo[_pid][msg.sender];

        // this would  be the amount if the user joined right from the start of the farm
        uint256 accumulatedBeets = (user.amount * pool.accBeetsPerShare) /
        ACC_BEETS_PRECISION;
        // subtracting the rewards the user is not eligible for
        uint256 eligibleBeets = accumulatedBeets - user.rewardDebt;

        // we set the new rewardDebt to the current accumulated amount of rewards for his amount of LP token
        user.rewardDebt = accumulatedBeets;

        if (eligibleBeets > 0) {
            safeBeetsTransfer(_to, eligibleBeets);
        }

        IRewarder _rewarder = rewarder[_pid];
        if (address(_rewarder) != address(0)) {
            _rewarder.onBeetsReward(
                _pid,
                msg.sender,
                _to,
                eligibleBeets,
                user.amount
            );
        }

        emit Harvest(msg.sender, _pid, eligibleBeets);
    }

    /// @notice Withdraw LP tokens from MCV and harvest proceeds for transaction sender to `_to`.
    /// @param _pid The index of the pool. See `poolInfo`.
    /// @param _amount LP token amount to withdraw.
    /// @param _to Receiver of the LP tokens and BEETS rewards.
    function withdrawAndHarvest(
        uint256 _pid,
        uint256 _amount,
        address _to
    ) public {
        PoolInfo memory pool = updatePool(_pid);
        UserInfo storage user = userInfo[_pid][msg.sender];

        require(_amount <= user.amount, "cannot withdraw more than deposited");

        // this would  be the amount if the user joined right from the start of the farm
        uint256 accumulatedBeets = (user.amount * pool.accBeetsPerShare) /
        ACC_BEETS_PRECISION;
        // subtracting the rewards the user is not eligible for
        uint256 eligibleBeets = accumulatedBeets - user.rewardDebt;

        /*
            after harvest & withdraw, he should be eligible for exactly 0 tokens
            => userInfo.amount * pool.accBeetsPerShare / ACC_BEETS_PRECISION == userInfo.rewardDebt
            since we are removing some LP's from userInfo.amount, we also have to remove
            the equivalent amount of reward debt
        */

        user.rewardDebt =
        accumulatedBeets -
        (_amount * pool.accBeetsPerShare) /
        ACC_BEETS_PRECISION;
        user.amount = user.amount - _amount;

        safeBeetsTransfer(_to, eligibleBeets);

        IRewarder _rewarder = rewarder[_pid];
        if (address(_rewarder) != address(0)) {
            _rewarder.onBeetsReward(
                _pid,
                msg.sender,
                _to,
                eligibleBeets,
                user.amount
            );
        }

        lpTokens[_pid].safeTransfer(_to, _amount);

        emit Withdraw(msg.sender, _pid, _amount, _to);
        emit Harvest(msg.sender, _pid, eligibleBeets);
    }

    // Withdraw without caring about rewards. EMERGENCY ONLY.
    function emergencyWithdraw(uint256 _pid, address _to) public {
        UserInfo storage user = userInfo[_pid][msg.sender];
        uint256 amount = user.amount;
        user.amount = 0;
        user.rewardDebt = 0;

        IRewarder _rewarder = rewarder[_pid];
        if (address(_rewarder) != address(0)) {
            _rewarder.onBeetsReward(_pid, msg.sender, _to, 0, 0);
        }

        // Note: transfer can fail or succeed if `amount` is zero.
        lpTokens[_pid].safeTransfer(_to, amount);
        emit EmergencyWithdraw(msg.sender, _pid, amount, _to);
    }

    // Safe BEETS transfer function, just in case if rounding error causes pool to not have enough BEETS.
    function safeBeetsTransfer(address _to, uint256 _amount) internal {
        uint256 beetsBalance = beets.balanceOf(address(this));
        if (_amount > beetsBalance) {
            beets.transfer(_to, beetsBalance);
        } else {
            beets.transfer(_to, _amount);
        }
    }

    // Update treasury address by the owner.
    function treasury(address _treasuryAddress) public onlyOwner {
        treasuryAddress = _treasuryAddress;
        emit SetTreasuryAddress(treasuryAddress, _treasuryAddress);
    }

    function updateEmissionRate(uint256 _beetsPerBlock) public onlyOwner {
        require(
            _beetsPerBlock <= 6e18,
            "maximum emission rate of 6 beets per block exceeded"
        );
        beetsPerBlock = _beetsPerBlock;
        emit UpdateEmissionRate(msg.sender, _beetsPerBlock);
    }
}
